let name=document.querySelector("#name"),email=document.querySelector("#email"),age=document.querySelector("#age");function checkName(){let e=/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,8})+$/.test(name.value);console.log(e),name.value&&!e||error("Text",name)}function checkEmail(){email.value}function checkAge(){age.value}function error(e,n){n.style.borderColor="red";let t=document.createElement("div");t.className="error",t.innerHTML=e,n.after(t)}function clear(){this.style.borderColor="#737171",this.nextSibling&&this.nextSibling.remove()}name.addEventListener("focusout",checkName),name.addEventListener("focusin",clear),email.addEventListener("focusout",checkEmail),name.addEventListener("focusin",clear),age.addEventListener("focusout",checkAge),name.addEventListener("focusin",clear);